<script>
import { removeToken } from '@/Untils/auth'
import { getUsername } from '@/Api/home'

export default {
  data() {
    return {
      i: false,
      isCollapse: false,
      list: [],
      username: '管理员'
    }
  },
  methods: {
    /*退出登录*/
    loginOut() {
      removeToken() //清除token
      this.$router.replace('/') // 跳转页面
    },
    // 导航栏逻辑
    addNav(val, title) {
      let obj = {
        val,
        title
      }
      let status = false
      this.list.forEach((ele) => {
        if (ele.title == obj.title) {
          status = true
        }
      })
      if (status == false) {
        this.list.push(obj)
      }
    },
    // 删除
    del(i) {
      this.list.splice(i, 1)
    }
  },
  created() {
    /*发起请求*/
    getUsername().then((res) => {
      console.log(res)
      this.username = res.data.data.username
    })
  }
}
